mapper.select:
    SELECT count(*) FROM image_ref


			uuid=#{uuid}


			and md5=#{md5}


			and userCode=#{userCode}


			and category=#{category}


			and validStatus = #{validStatus}

select * from image_ref


		uuid=#{uuid}


		and md5=#{md5}


		and userCode=#{userCode}


		and category=#{category}


		and validStatus = #{validStatus}



order by updateTime desc
limit #{offset},#{pageSize}
